import ms from 'ms'
import AbortController from 'abort-controller'
import { mergeRequestOptions, retry } from './Helper'
import crossFetch from 'cross-fetch'

import {
  CompleteRequestOptions,
  FETCH_BUFFER_DEFAULTS,
  FETCH_JSON_DEFAULTS,
  getAllHeaders,
  POST_DEFAULTS,
  RequestOptions
} from './FetcherConfiguration'

export class Fetcher {
  customDefaults: Omit<RequestOptions, 'body'>

  constructor(customDefaults?: Omit<RequestOptions, 'body'>) {
    this.customDefaults = customDefaults ?? {}
  }

  overrideDefaults(overrideDefaults: Omit<RequestOptions, 'body'>): void {
    this.customDefaults = mergeRequestOptions(this.customDefaults, overrideDefaults)
  }

  fetch(url: string, options?: Partial<CompleteRequestOptions>): Promise<Response> {
    return fetchInternal(url, {
      method: 'get',
      // it is better to not assume how this generic fetch will be used, not sending retries
      attempts: 1,
      // no timeout by default
      timeout: '0s',
      // no wait-time by default
      waitTime: '0s',
      ...mergeRequestOptions(this.customDefaults, options)
    })
  }

  /**
   * @deprecated please use Fetcher.fetch instead
   */
  fetchJson(url: string, options?: RequestOptions): Promise<unknown> {
    return fetchJson(url, mergeRequestOptions(this.customDefaults, options))
  }

  /**
   * @deprecated please use Fetcher.fetch instead
   */
  fetchBuffer(url: string, options?: RequestOptions): Promise<Buffer> {
    return fetchBuffer(url, mergeRequestOptions(this.customDefaults, options))
  }

  /**
   * Fetches the url and pipes the response obtained from the upstream to the `writeTo` Stream and
   * returns the headers from the upstream request.
   * IMPORTANT: THIS METHOD DOES NOT AWAIT THE PIPE TO FINISH. THE PROMISE FULFILLS RIGHT AFTER WE RECEIVE THE HEADERS.
   * @param url to request
   * @param writeTo the stream to pipe the response to
   * @param options config for the request
   * @deprecated please use Fetcher.fetch instead
   */
  fetchPipe(url: string, writeTo: any, options?: RequestOptions): Promise<Headers> {
    return fetchPipe(url, writeTo, mergeRequestOptions(this.customDefaults, options))
  }

  /**
   * @deprecated please use Fetcher.fetch instead
   */
  postForm(url: string, options?: RequestOptions): Promise<unknown> {
    return postForm(url, mergeRequestOptions(this.customDefaults, options))
  }

  queryGraph<T = any>(
    url: string,
    query: string,
    variables: Record<string, any>,
    options?: RequestOptions
  ): Promise<T> {
    return queryGraph(url, query, variables, mergeRequestOptions(this.customDefaults, options))
  }

  // Clones the fetcher and creates a new one
  clone(): Fetcher {
    return new Fetcher(this.customDefaults)
  }
}

export async function fetchJson(url: string, options?: RequestOptions): Promise<unknown> {
  const response = await fetchInternal(url, mergeRequestOptions(FETCH_JSON_DEFAULTS, options))
  return response.json()
}

export async function fetchArrayBuffer(url: string, options?: RequestOptions): Promise<Uint8Array> {
  const response = await fetchInternal(url, mergeRequestOptions(FETCH_BUFFER_DEFAULTS, options))
  return new Uint8Array(await response.arrayBuffer())
}

/**
 * @deprecated use fetchArrayBuffer instead
 */
export async function fetchBuffer(url: string, options?: RequestOptions): Promise<Buffer> {
  const response = await fetchInternal(url, mergeRequestOptions(FETCH_BUFFER_DEFAULTS, options))
  return Buffer.from(await response.arrayBuffer())
}

/**
 * Fetches the url and pipes the response obtained from the upstream to the `writeTo` Stream and
 * returns the headers from the upstream request.
 * IMPORTANT: THIS METHOD DOES NOT AWAIT THE PIPE TO FINISH. THE PROMISE FULFILLS RIGHT AFTER WE RECEIVE THE HEADERS.
 * @param url to request
 * @param writeTo the stream to pipe the response to
 * @param options config for the request
 */
export async function fetchPipe(url: string, writeTo: any, options?: RequestOptions): Promise<Headers> {
  const response = await fetchInternal(url, mergeRequestOptions(FETCH_BUFFER_DEFAULTS, options))

  if (!response.body) throw new Error('The function fetchPipe only works in Node.js compatible enviroments')

  if ('pipe' in response.body) {
    ;(response.body as any).pipe(writeTo)
    return response.headers
  }

  throw new Error('The function fetchPipe only works in Node.js compatible enviroments')
}

export async function postForm(url: string, options?: RequestOptions): Promise<unknown> {
  const res = await fetchInternal(url, mergeRequestOptions(POST_DEFAULTS, options))
  return res.json()
}

export type GraphQLResponse = {
  errors: any[]
  data: any
}

export async function queryGraph<T = any>(
  url: string,
  query: string,
  variables: Record<string, any>,
  options?: RequestOptions
): Promise<T> {
  const response = (await postForm(url, {
    body: JSON.stringify({ query, variables }),
    headers: { 'Content-Type': 'application/json' },
    ...options
  })) as GraphQLResponse
  if (response.errors) {
    throw new Error(`Error querying graph. Reasons: ${JSON.stringify(response.errors)}`)
  }
  return response.data
}

async function identity<T>(a: T): Promise<T> {
  return a
}

/**
 * This is the method where everything happens, all of the methods in this file call internally fetchInternal.
 * If you need to modify something for all requests, do it here.
 */
async function fetchInternal(url: string, options: CompleteRequestOptions): Promise<Response> {
  return retry(
    async () => {
      const controller = new AbortController()
      const transformRequest = options.requestMiddleware || identity
      const transformResponse = options.responseMiddleware || identity

      const request = await transformRequest({
        requestInfo: url,
        requestInit: {
          signal: controller.signal,
          body: options.body,
          method: options.method,
          headers: getAllHeaders(options)
        }
      })

      // schedule timeout right after transforming Request
      const timeoutTime = ms(options.timeout)
      const timeout = timeoutTime
        ? setTimeout(() => {
            controller.abort()
          }, timeoutTime)
        : 0

      try {
        const response: Response = await crossFetch(request.requestInfo, request.requestInit)
        if (response.ok) {
          return await transformResponse(response)
        } else {
          const responseText = await response.text()
          throw new Error(`Failed to fetch ${url}. Got status ${response.status}. Response was '${responseText}'`)
        }
      } finally {
        if (timeout) clearTimeout(timeout)
      }
    },
    options.attempts,
    options.waitTime
  )
}

import ms from 'ms'
import { CompleteRequestOptions, RequestOptions } from './FetcherConfiguration'

export function delay(time: string): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms(time)))
}

export async function retry<T>(
  execution: () => Promise<T>,
  attempts: number,
  waitTime: string = '1s',
  failedAttemptCallback?: (attemptsLeft: number) => void
): Promise<T> {
  while (attempts > 0) {
    try {
      return await execution()
      //     ^^^^^ never remove this "await" keyword, otherwise this function won't
      //           catch the exception and perform the retries
    } catch (error) {
      attempts--
      if (attempts > 0) {
        if (failedAttemptCallback) {
          failedAttemptCallback(attempts)
        }
        await delay(waitTime)
      } else {
        throw error
      }
    }
  }
  throw new Error('Please specify more than one attempt for the retry function')
}

/** Add defaults to missing properties in the partial object */
export function applyDefaults<T, K = T | Partial<T>>(defaults: K, partial?: Partial<T>): K {
  return { ...defaults, ...partial }
}

/** Add some defaults to missing properties in the partial object. This means that the object is not yet complete */
export function applySomeDefaults<T>(defaults: Partial<T>, partial?: Partial<T>): Partial<T> {
  return { ...defaults, ...partial }
}

/**  As headers field is Record<string, string> type, then when merging request Options
     it's needed to merge the array instead of just applying the defaults.           */
export function mergeRequestOptions<T = CompleteRequestOptions | RequestOptions>(
  target: T,
  source?: RequestOptions
): T {
  const combinedHeaders: Record<string, string> = {
    ...(target as RequestOptions)?.headers,
    ...source?.headers
  }
  const combinedCookies: Record<string, string> = {
    ...(target as RequestOptions)?.cookies,
    ...source?.cookies
  }
  return { ...target, ...source, headers: combinedHeaders, cookies: combinedCookies }
}

export type RequestOptions = Partial<CompleteRequestOptions>
import cookie from 'cookie'

export type CrossFetchRequest = {
  requestInfo: string | RequestInfo
  requestInit?: RequestInit
}

export type CompleteRequestOptions = {
  method: string
  attempts: number // Number of attempts to perform the request
  timeout: string // Time to abort the request. Time format accepted by ms: Examples: '0.5s', '2m', '3h', '100' (assumed to be milliseconds)
  waitTime: string // Time to wait between attempts. Time format accepted by ms: Examples: '0.5s', '2m', '3h', '100' (assumed to be milliseconds)
  body?: FormData | string
  headers?: Record<string, string>
  cookies?: Record<string, string>
  // Configure a lambda to execute with the request, before executing it.
  // This is used when you need to configure something of the fetcher according to the generated request.
  requestMiddleware?: (request: CrossFetchRequest) => Promise<CrossFetchRequest>
  // Configure a lambda to execute with the response if it was okay.
  // This is used when you need to configure something of the fetcher according to the response obtained.
  responseMiddleware?: (response: Response) => Promise<Response>
}

export function getAllHeaders(options: CompleteRequestOptions): Record<string, string> {
  const headers = options.headers || {}
  if (options.cookies && Object.entries(options.cookies).length > 0) {
    headers['Cookie'] = Object.entries(options.cookies)
      .map((entry) => cookie.serialize(entry[0], entry[1]))
      .reduce((a, b) => `${a}; ${b}`)
  }
  return headers
}

export const FETCHER_DEFAULTS = {
  method: 'GET',
  attempts: 1,
  waitTime: '1s',
  timeout: '5m'
}

export const FETCH_JSON_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  timeout: '30s',
  waitTime: '0.5s'
}
export const FETCH_BUFFER_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  timeout: '1m'
}
export const POST_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  method: 'POST'
}

import * as hashing from '@dcl/hashing'
import { ContentFileHash } from '../types'

export namespace Hashing {
  /**
   * Given a set of files, return a map with their hash
   * @deprecated use calculateIPFSHashes instead. This function only exists for compatibility reasons
   */
  export async function calculateHashes<T extends Uint8Array>(
    files: T[]
  ): Promise<{ hash: ContentFileHash; file: T }[]> {
    const entries = Array.from(files).map(async (file) => ({
      hash: await calculateBufferHash(file),
      file
    }))
    return Promise.all(entries)
  }

  /**
   * Return the given buffer's hash
   * @deprecated use calculateIPFSHash instead. This function only exists for compatibility reasons
   */
  export async function calculateBufferHash(buffer: Uint8Array): Promise<ContentFileHash> {
    return hashing.hashV0(buffer)
  }

  /**
   * @deprecated use `import {hashV1} from '@dcl/hashing'` instead
   */
  export async function calculateIPFSHash(buffer: Uint8Array): Promise<ContentFileHash> {
    return hashing.hashV1(buffer)
  }

  export async function calculateIPFSHashes<T extends Uint8Array>(
    files: T[]
  ): Promise<{ hash: ContentFileHash; file: T }[]> {
    const entries = Array.from(files).map(async (file) => ({
      hash: await hashing.hashV1(file),
      file
    }))
    return Promise.all(entries)
  }

  /**
   * Calculates the content hash of multiple files to be used consistently by the builder
   * and other content-based applications when hashes need to be stored on-chain.
   *
   * Returns the CIDv1 of the data prepared to sign
   *
   * @deprecated use `import {calculateMultipleHashesADR32} from '@dcl/hashing'` instead
   */
  export const calculateMultipleHashesADR32 = hashing.calculateMultipleHashesADR32

  /**
   * Calculates the content hash of multiple files to be used consistently by the builder
   * and other content-based applications when hashes need to be stored on-chain.
   * @deprecated this is maintained only for compatibility reasons with calculateBufferHash (Qm prefix)
   */
  export const calculateMultipleHashesADR32LegacyQmHash = hashing.calculateMultipleHashesADR32LegacyQmHash
}

import { CatalystData, DAOContract } from '../contracts/CatalystContract'
